import { HttpClient, HttpHeaders } from '@angular/common/http';
import { throwError } from 'rxjs';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "../core/auth/services/auth.service";
const httpOptions = {
    headers: new HttpHeaders({
        'Content-Type': 'application/json',
        Authorization: localStorage.getItem('access_token')
    })
};
export class ApiService {
    constructor(http, authService, handler) {
        this.http = http;
        this.authService = authService;
        this.user_id = JSON.parse(localStorage.getItem("user")).id;
        this.token = this.authService.getToken();
        this.url = "https://api.jikan.moe/v3/anime/20";
        this.http = new HttpClient(handler);
    }
    async setAnimeId(name) {
        const reqUrl = "https://api.jikan.moe/v3/search/anime?q=" + name + "&page=1&limit=1";
        const response = await (await fetch(reqUrl)).json();
        this.anime_id = await response.results[0].mal_id;
        console.log(this.anime_id);
    }
    setUserAnime(name) {
        this.setAnimeId(name);
        return this.http.get(this.url);
    }
    handleError(error) {
        if (error.status === 0) {
            //Client-side or network
            console.error('An error occurred:', error.error);
        }
        else {
            //Back-end
            console.error(`Backend returned code ${error.status}, body was: `, error.error);
        }
        return throwError('Something bad happened; please try again later.');
    }
}
