import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "../core/auth/services/auth.service";
export class ApiService {
    constructor(http, authService) {
        this.http = http;
        this.authService = authService;
        this.user_id = JSON.parse(localStorage.getItem("user")).id;
        this.token = this.authService.getToken();
        this.url = "https://api.jikan.moe/v3/anime/";
    }
    async setAnimeId(name) {
        const response = await (await fetch("https://api.jikan.moe/v3/search/anime?q=" + name + "&page=1?page=1&limit=1")).json();
        this.anime_id = response.results[0].mal_id;
    }
    async addUserAnime(name) {
        await this.setAnimeId(name);
        let userAnime = {
            "user": { id: this.user_id },
            "anime": { id: this.anime_id }
        };
        const userAnimeJSON = JSON.stringify(userAnime);
        fetch("http://localhost:8090/anilib/library", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: this.token
            },
            body: userAnimeJSON
        })
            .then(response => response.json())
            .then(data => {
            console.log('Success', data);
        })
            .catch((error) => {
            console.error('Unable to post userAnime', error);
        });
    }
}
