import { HttpHeaders } from '@angular/common/http';
import { throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "../core/auth/services/auth.service";
import * as i3 from "../core/auth/models/user-anime";
const httpOptions = {
    headers: new HttpHeaders({
        'Content-Type': 'application/json',
        Authorization: localStorage.getItem('access_token')
    })
};
export class ApiService {
    constructor(http, authService, userAnime) {
        this.http = http;
        this.authService = authService;
        this.userAnime = userAnime;
        this.user_id = JSON.parse(localStorage.getItem("user")).id;
        this.token = this.authService.getToken();
        this.url = "https://api.jikan.moe/v3/anime/";
    }
    async setAnimeId(name) {
        const response = await (await fetch("https://api.jikan.moe/v3/search/anime?q=" + name + "&page=1?page=1&limit=1")).json();
        this.anime_id = response.results[0].mal_id;
    }
    // async addUserAnime(name: string){
    //   await this.setAnimeId(name)
    //   let userAnime: any = {
    //     "user":{"id":61},
    //     "anime":{"id":225}
    //   } 
    //   console.log(JSON.stringify(userAnime))
    //   fetch("http://localhost:8090/anilib/library", {
    //     method: 'POST',
    //     body: userAnime,
    //     headers: {
    //       'Content-Type': 'application/json',
    //       Authorization: this.token
    //     }
    //   })
    //   .then(response => response.json())
    //   .then(data => {
    //     console.log('Success', data)
    //   })
    //   .catch((error) =>{
    //     console.error('Unable to post userAnime', error)
    //   })
    // }
    addUserAnime(name) {
        let uAInfo = {
            "user": { "id": 61 },
            "anime": { "id": 225 }
        };
        this.userAnime = uAInfo;
        return this.http.post("http://localhost:8090/anilib/library", this.userAnime, httpOptions)
            .pipe(retry(3), catchError(this.handleError));
    }
    handleError(error) {
        if (error.status === 0) {
            //Client-side or network
            console.error('An error occurred:', error.error);
        }
        else {
            //Back-end
            console.error(`Backend returned code ${error.status}, body was: `, error.error);
        }
        return throwError('Something bad happened; please try again later.');
    }
}
