import { HttpHeaders } from '@angular/common/http';
import { Genre } from 'src/app/core/auth/models/genre';
import { Studio } from 'src/app/core/auth/models/studio';
import { UserAnime } from 'src/app/core/auth/models/user-anime';
import { Anime } from 'src/app/shared/models/anime';
import * as i0 from "@angular/core";
import * as i1 from "src/app/shared/api.service";
import * as i2 from "@angular/common/http";
const httpOptions = {
    headers: new HttpHeaders({
        'Content-Type': 'application/json',
        Authorization: localStorage.getItem('access_token')
    })
};
export class RecommenderComponent {
    constructor(apiService, http) {
        this.apiService = apiService;
        this.http = http;
        this.temp = localStorage.getItem('user');
        this.user = JSON.parse(this.temp);
    }
    async testOutput() {
    }
    ngOnInit() {
        this.apiService.getUserAnime("naruto").subscribe((data) => {
            console.log(data);
            let studio = new Studio();
            studio = {
                id: data.studios[0].mal_id,
                name: data.studios[0].name
            };
            let genre = new Genre();
            genre = {
                id: data.themes[0].mal_id,
                name: data.themes[0].name
            };
            let anime = new Anime(data.mal_id, data.title, data.rating, data.score, data.synopsis, genre, studio);
            this.testAnime = anime;
            console.log(this.testAnime);
            this.addUserAnime();
            // this.testUserAnime.anime = this.testAnime
            // console.log(this.testUserAnime)
        });
    }
    addUserAnime() {
        let userAnime = new UserAnime();
        userAnime = {
            user: this.user,
            anime: this.testAnime,
        };
        console.log(userAnime);
        this.http.post("http://localhost:8090/anilib/library", userAnime, httpOptions).subscribe();
    }
}
