export class LoginComponent {
    constructor(formBuilder, http, router) {
        this.formBuilder = formBuilder;
        this.http = http;
        this.router = router;
        this.loginEndpoint = "http://localhost:8090/anilib/login";
        this.userEndpoint = "http://localhost:8090/anilib/user/";
    }
    ngOnInit() {
        this.loginForm = this.formBuilder.group({
            username: [''],
            password: ['']
        });
    }
    login() {
        this.http.post(this.loginEndpoint, this.loginForm.getRawValue())
            .subscribe((res) => {
            this.setAccessToken(res.token).then(() => {
                this.http.get(this.userEndpoint + this.loginForm.get('username').value).subscribe((res) => {
                    localStorage.setItem('user', JSON.stringify(res));
                });
                this.loginForm.reset();
                this.router.navigate([`/library`]);
            });
        }, err => {
            alert("Something went wrong");
        });
    }
    async setAccessToken(accessToken) {
        await localStorage.setItem('access_token', accessToken);
    }
}
